def bin_search(a, key):
    pl = 0
    pr = len(a)-1

    while True:
        pc = (pl + pr) // 2 #나눴을때의 몫으로 이동, 소숫점으로 나와서 이동하는 것을 방지하기위해서
        if a[pc] == key:
            return pc
        elif a[pc]  pr:
            break

    return -1


